import React, { useEffect, useState } from 'react';
import BestComment from '../bestComment/BestComment';
import LikeDislike from '../like_dislike/LikeDislike';
import MenuName from '../menuName/MenuName';
import Picture from '../picture/Picture';
import State from '../state/State';
import './style.css';

export default function MenuCard({ pictureUrl, menuName, initialState, bestComment, likes, dislikes }) {
  const [state, setState] = useState(initialState);
  const [comment, setComment] = useState(bestComment);
  const [likeCount, setLikeCount] = useState(likes);
  const [dislikeCount, setDislikeCount] = useState(dislikes);

  // 상태 업데이트를 위한 WebSocket 연결
  useEffect(() => {
    const socket = new WebSocket('wss://your-websocket-server');
    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.menuName === menuName) {
        setState(data.state);
        setComment(data.bestComment);
        setLikeCount(data.likes);
        setDislikeCount(data.dislikes);
      }
    };

    return () => socket.close();
  }, [menuName]);

  return (
    <div className="frame">
      <Picture url={pictureUrl} />
      <MenuName menuName={menuName} />
      <State getSignal={state} />
      <BestComment bestComment={comment} />
      <LikeDislike downRate={dislikeCount} upRate={likeCount} />
    </div>
  );
}
