import React, { useEffect, useState } from 'react';
import MenuCard from '../menuCard/MenuCard';
import './style.css';
import menuData from './menuData.json'; // JSON 데이터 임포트

export default function MenuCardList() {
  const [menus, setMenus] = useState(menuData);

  // 메뉴 초기 로딩
  useEffect(() => {
    // 메뉴 데이터를 서버에서 가져오는 경우
    // fetch('/api/menu')
    //   .then(response => response.json())
    //   .then(data => setMenus(data));
  }, []);

  return (
    <div className="menu_card_list">
      <MenuCard
        pictureUrl={menus[0].pictureUrl}
        corner={menus[0].corner}
        menuName={menus[0].menuName}
        initialState={menus[0].state}
        bestComment={menus[0].bestComment}
        likes={menus[0].likes}
        dislikes={menus[0].dislikes}
      />
      <MenuCard
        pictureUrl={menus[1].pictureUrl}
        corner={menus[1].corner}
        menuName={menus[1].menuName}
        initialState={menus[1].state}
        bestComment={menus[1].bestComment}
        likes={menus[1].likes}
        dislikes={menus[1].dislikes}
      />
      <MenuCard
        pictureUrl={menus[2].pictureUrl}
        corner={menus[2].corner}
        menuName={menus[2].menuName}
        initialState={menus[2].state}
        bestComment={menus[2].bestComment}
        likes={menus[2].likes}
        dislikes={menus[2].dislikes}
      />
      <MenuCard
        pictureUrl={menus[3].pictureUrl}
        corner={menus[3].corner}
        menuName={menus[3].menuName}
        initialState={menus[3].state}
        bestComment={menus[3].bestComment}
        likes={menus[3].likes}
        dislikes={menus[3].dislikes}
      />
      <MenuCard
        pictureUrl={menus[4].pictureUrl}
        corner={menus[4].corner}
        menuName={menus[4].menuName}
        initialState={menus[4].state}
        bestComment={menus[4].bestComment}
        likes={menus[4].likes}
        dislikes={menus[4].dislikes}
      />
    </div>
  );
}
