import React, { useState } from 'react';
import './style.css';
import thumbDown from './images/thumb-down.svg';
import thumbUp from './images/thumb-up.svg';

const LikeDislike = ({ downRate, upRate, onLike, onDislike }) => {
  const [hasLiked, setHasLiked] = useState(false);
  const [hasDisliked, setHasDisliked] = useState(false);

  const handleLike = () => {
    if (!hasLiked) {
      onLike();
      setHasLiked(true);
      if (hasDisliked) {
        setHasDisliked(false);
      }
    }
  };

  const handleDislike = () => {
    if (!hasDisliked) {
      onDislike();
      setHasDisliked(true);
      if (hasLiked) {
        setHasLiked(false);
      }
    }
  };

  return (
    <div className="rate">
      <div className="item" onClick={handleDislike}>
        <img src={thumbDown} alt="Thumb Down" />
        <p>{downRate}</p>
      </div>
      <div className="item" onClick={handleLike}>
        <img src={thumbUp} alt="Thumb Up" />
        <p>{upRate}</p>
      </div>
    </div>
  );
};

export default LikeDislike;
